<template>
  <div>

    <Row>
      <Card>
        <Icon type="md-list" size="18"/>
        与我相关的审核记录 <span style="float: right">共 {{ total }} 条 &nbsp;&nbsp;</span>
        <Divider style="margin: 15px 0"/>
        <Table ref="myTable" border :columns="table_header" :data="table_content">

          <template slot="dev_team_render" slot-scope="props">
            <Tag v-for="item in props.dev_team" :key="item" color="default">
              {{item.split('-')[item.split('-').length-1]}}
            </Tag>
          </template>


          <template slot="title_render" slot-scope="props">
            <router-link :to="{name: 'database_audit_detail', params: {id: props.id}}">{{props.title}}
            </router-link>
          </template>

          <template slot="database_render" slot-scope="props">
            <Tag v-for="item in props.database" :key="item" color="default">
              {{item}}
            </Tag>
          </template>

          <template slot="submit_user_render" slot-scope="props">
            {{props.submit_user}}
          </template>

          <template slot="check_users_render" slot-scope="props">
            {{props.users}}
          </template>


          <template slot="users_render" slot-scope="props">
          <span align="center" v-for="user in props.users" :key="user">
            <span v-if="user == realname" style="font-weight:bold">{{user}} &nbsp;&nbsp; </span>
            <span v-else>{{user}}&nbsp;&nbsp;</span>
          </span>
          </template>

          <template slot="status_render" slot-scope="props">
            <Tag type="dot" v-if="props.status=='待审核'" color="warning">{{props.status}}</Tag>
            <Tag type="dot" v-else-if="props.status=='不通过'" color="error">{{props.status}}</Tag>
            <Tag type="dot" v-else-if="props.status=='已通过'" color="success">{{props.status}}</Tag>
            <Tag type="dot" v-else-if="props.status=='已上线'" color="#a917a6">{{props.status}}</Tag>
            <Tag type="dot" v-else color="primary">{{props.status}}</Tag>
          </template>

        </Table>
        <div style="margin: 10px;overflow: hidden">
          <div style="float: right;">
            <Page :total="total" :current.sync="current" @on-change="changePage"></Page>
          </div>
        </div>

      </Card>
    </Row>

  </div>
</template>


<script>

  import {getChangeOrderList} from '@/api/database'
  import {formatDateYYYMMDD} from '@/lib/tools'
  import {mapState, mapGetters, mapActions} from 'vuex'

  export default {
    data() {
      return {
        table_header: [
          {
            title: '编号',
            key: 'id',
            maxWidth: 60
          },
          {
            title: '团队',
            key: 'dev_team',
            maxWidth: 150,
            render: (h, params) => {
              return h(
                'div',
                this.$refs.myTable.$scopedSlots.dev_team_render({dev_team: JSON.parse(params.row.dev_team)})
              )
            }
          },
          {
            title: '标题',
            key: 'title',
            render: (h, params) => {
              return h(
                'div',
                this.$refs.myTable.$scopedSlots.title_render({
                  title: params.row.title,
                  id: params.row.id,
                  sql_files: params.row.sql_files
                })
              )
            }
          },
          {
            title: '涉及的数据库',
            key: 'database',
            render: (h, params) => {
              return h(
                'div',
                this.$refs.myTable.$scopedSlots.database_render({
                  database: params.row.database,
                })
              )
            }
          },
          {
            title: '提交人员',
            key: 'submit_user',
            maxWidth: 100,
            render: (h, params) => {
              return h(
                'div',
                this.$refs.myTable.$scopedSlots.users_render({
                  users: [params.row.submit_user],
                })
              )
            }
          },
          {
            title: '审核人员',
            key: 'check_users',
            maxWidth: 100,
            render: (h, params) => {
              return h(
                'div',
                this.$refs.myTable.$scopedSlots.users_render({
                  users: JSON.parse(params.row.check_users),
                })
              )
            }
          },
          {
            title: '抄送人员',
            key: 'cc_users',
            maxWidth: 100,
            render: (h, params) => {
              return h(
                'div',
                this.$refs.myTable.$scopedSlots.users_render({
                  users: JSON.parse(params.row.cc_users),
                })
              )
            }
          },
          {
            title: '提交日期',
            key: 'create_time',
            maxWidth: 105
          },
          {
            title: '上线日期',
            key: 'online_time',
            maxWidth: 105

          },
          {
            title: '当前状态',
            key: 'status',
            maxWidth: 130,
            render: (h, params) => {
              return h(
                'div',
                this.$refs.myTable.$scopedSlots.status_render({status: params.row.status})
              )
            }
          }
        ],
        table_content: [],
        formItem: {},
        search_data: {
          dev_team__icontains: '',
          status: '',
          create_time: '',
          online_time: ''
        },
        search_query: {
          offset: 0
        },
        total: 0,
        current: 1


      }
    },
    computed: {
      ...mapGetters([
        'realname',
      ]),
    },
    methods: {
      changePage(page) {
        this.search_query.offset = (page - 1) * 10
        this.search_change('from_page')
      },

      search_change_create_time(val, type) {
        this.search_data.create_time = val
        this.search_change()
      },
      search_change_online_time(val, type) {
        this.search_data.online_time = val
        this.search_change()
      },
      getflowTesttaskList({data, query}) {
        data = {
          "submit_user__icontains": "\"" + this.realname + "\"",
          "check_users__icontains": "\"" + this.realname + "\"",
          "cc_users__icontains": "\"" + this.realname + "\"",
          "type": "Q"
        }
        getChangeOrderList({data, query}).then(res => {
          if (res.data.status) {
            this.table_content = res.data.data
            this.total = res.data.count
          } else {

          }
        });
      },
      search_change(val) {

        if (val != 'from_page') {
          this.search_query.offset = 0
          this.current = 1
        }


        let data = {...this.search_data}
        for (let i in data) {
          if (data[i] == '' || JSON.stringify(data[i]) == '["",""]') {
            delete data[i]
          }


        }
        let query = this.search_query

        this.getflowTesttaskList({data, query})
      }
    },
    mounted: function () {
      this.getflowTesttaskList({})
    }
  }
</script>

